import React from "react";
import Header from "./components/Header";
import MainVideo from "./components/MainVideo";
import VideoDescription from "./components/VideoDescription";
import NewComment from "./components/NewComment";
import CommentsToBePosted from "./components/CommentsToBePosted";
import VideoList from "./components/VideoList/VideoList";
// import NextUpVideos from "./components/NextUpVideos";
import "./styles/main.css";

function App() {
  const sidebarVideos = [
    {
      image: "../assets/images/video-list-1.jpg",
      title: `Become A Travel Pro In 7O0npxe Easy Lesson`,
      name: `Scotty Cranmer`,
    },
    {
      image: "../assets/images/video-list-2.jpg",
      title: `Les Houches The Hidden Gem Of The Chamonix`,
      name: `Scotty Cranmer`,
    },
    {
      image: "../assets/images/video-list-3.jpg",
      title: `Travel Health Useful Medical Information For`,
      name: `Scotty Cranmer`,
    },
    {
      image: "../assets/images/video-list-4.jpg",
      title: `Cheap Airline Tickets Great Ways To Save`,
      name: `Emily Harper`,
    },
    {
      image: "../assets/images/video-list-5.jpg",
      title: `Take A Romantic Break In A Boutique Hotel`,
      name: `Ethan Owen`,
    },
    {
      image: "../assets/images/video-list-6.jpg",
      title: `Choose The Perfect Accommodations`,
      name: `Lydia Perez`,
    },
    {
      image: "../assets/images/video-list-7.jpg",
      title: `Cruising Destination Ideas`,
      name: `Timothy Austin`,
    },
    {
      image: "../assets/images/video-list-8.jpg",
      title: `Train Travel On Track For Safety`,
      name: `Scotty Cranmer`,
    },
  ];

  return (
    <div className="App">
      <Header />
      <MainVideo />
      <VideoDescription />
      <NewComment />
      <CommentsToBePosted />
      <VideoList videos={sideBarVideos} />
      {/* <NextUpVideos /> */}
    </div>
  );
}

export default App;
