import React from "react";

const NewComment = () => {
  return (
    <section className="new-comment">
      <p className="new-comment__number-of-comments">3 Comments</p>
      <p className="new-comment-form-title">JOIN THE CONVERSATION </p>
      <form className="new-comment__form">
        <label htmlFor="textArea" className="new-comment__label"></label>
        <textarea name="textArea" className="new-comment__textarea"></textarea>
        <button type="submit" className="new-comment__submit-btn">
          COMMENT
        </button>
      </form>
    </section>
  );
};

export default NewComment;
